import React from 'react';
import "./index.css";


const Home = () => {
  return (
    <div>
      <h1 className="header">MENTOR CONNECT</h1>
      <h1 className='h1-categories'>Benefits:</h1>
      <p className='p-homes'><strong>1. Decentralized Learning:</strong> Harness the power of blockchain technology for secure, transparent, and decentralized learning experiences.</p>
      <p className='p-homes'><strong>2. Personalized Education:</strong> Tailor your learning experience by connecting with experienced mentors or tutors who match your unique needs.</p>
      <p className='p-homes'><strong>3. Verified Tutors and Mentors:</strong> We ensure that all our tutors and mentors are qualified and verified, giving you peace of mind.</p>
      <p className='p-homes'><strong>4. Transparent Transactions:</strong> Every tutoring session and payment is recorded on the blockchain for complete transparency and accountability.</p>
      <h1 className='h1-categories'>Why Choose Us:</h1>
      <p className='p-homes'><strong>1. Seamless Connectivity:</strong> Easily connect with tutors and mentors from around the world in real-time.</p>
      <p className='p-homes'><strong>2. Secure Payments:</strong> Pay for your sessions securely with cryptocurrency, ensuring the safety of your financial information.</p>
      <p className='p-homes'><strong>3. Track Progress:</strong> Monitor your progress and learning milestones through our user-friendly dashboard.</p>
      <p className='p-homes'><strong>4. Community Engagement:</strong> Join a vibrant community of learners and educators, fostering a culture of knowledge sharing.</p>
      <h1 className="header">Get Started Today!</h1>
      <h1 className="h4-home">"Education is the passport to the future, for tomorrow belongs to those who prepare for it today." </h1>
      <a href="/signinActivist" class="hero-btn">Sign In As Mentor</a>
      <a href="/signinfarmer" class="hero-btn">Sign In As Tutor</a>
      <a href="/signinowner" class="hero-btn">Sign In As Admin</a>
    </div>
  );
};

export default Home;
