import './App.css';
import { useState } from 'react';
import withdrawFarmer from './withdrawFarmer';
import DepositFarmer from './DepositFarmer';
import VoteFarmer from './VoteFarmer';
import NameFarmer from './NameFarmer';
import Areaname from './Areaname';
import "./farmer.css"

const Activist = () => {
  let [withdraw, setWithdraw] = useState('');
  let [deposit, setDeposit] = useState('');
  let [message, setMessage] = useState('');
  let [name, setName] = useState('');
  let [areaname, setAreaname] = useState('');
  let [AreaAffected, setAreaAffected] = useState('')

  const handleChange = event => {
    setWithdraw(event.target.value);
    console.log('value is:', event.target.value);
  };

  const handleDeposit = event => {
    setDeposit(event.target.value);
    console.log('value is:', event.target.value);
  }

  const handleMessage = event => {
    setMessage(event.target.value);
    console.log('value is:', event.target.value);
  }

  const handleName = event => {
    setName(event.target.value);
    console.log('value is:', event.target.value);
  }

  const handleAreaname = event => {
    setAreaname(event.target.value);
    localStorage.setItem('affected', event.target.value)

    console.log('value is:', event.target.value);
  }

  const handleAreaaffected = event => {
    setAreaAffected(event.target.value);
    localStorage.setItem('affected1', event.target.value)

    console.log('value is:', event.target.value);
  }

  return (
    <div className="App">
      <h1>Student</h1>
      <div>
        <p className='f-head'>Name: {name}</p>
        <input
          type="text"
          id="message"
          name="message"
          className='txt-msg'
          placeholder='Name'
          onChange={handleName}
          value={name}></input>
      </div>
      <div>
        <p className='f-head'>Roll No: {name}</p>
        <input
          type="text"
          id="message"
          name="message"
          className='txt-msg'
          placeholder='RollNo'
          onChange={handleName}
          value={name}></input>
      </div>
      <div>
        <p className='f-head'>Department: {name}</p>
        <input
          type="text"
          id="message"
          name="message"
          className='txt-msg'
          placeholder='Department'
          onChange={handleName}
          value={name}></input>
      </div>
      <div>
        <p className='f-head'>Message: {name}</p>
        <input
          type="text"
          id="message"
          name="message"
          className='txt-msg'
          placeholder='Message'
          onChange={handleName}
          value={name}></input>
      </div>
      <div>
        <p className='f-head'>Mentor Name:- {name}</p>
        <input
          type="text"
          id="message"
          name="message"
          className='txt-msg'
          placeholder='MentorName'
          onChange={handleName}
          value={name}></input>
      </div>
      <div>
        <p className='f-head'>Deposit amount: {deposit}</p>
        <input
          type="text"
          id="message"
          name="message"
          className='txt-msg'
          placeholder='Deposit amount'
          onChange={handleDeposit}
          value={deposit}></input>
        <button onClick={DepositFarmer}{...() => deposit}>Deposit</button>
      </div>
    </div>
  );
}

export default Activist;
